import React ,{Component} from 'react';
import logo from './logo.svg';
import './App.css';
import axios from 'axios';

class App extends Component {

constructor(props) {
super(props);

this.state = {
amount: 1,
//country: '',
tocountry: '',
frcountry: '',
convertedValue: 0,
currencyCodes: []
}
}

componentWillMount = () => {
console.log("will mount")

}

componentDidMount = () => {
console.log("did mount");
axios({
method: "GET",
url: "https://free.currconv.com/api/v7/currencies?apiKey=b2926387e0a03a02fa74"
}).then((res) => {
let countryCodes = [];
// console.log(res.data.results);
let currencyCodes = Object.keys(res.data.results);
// console.log(currencyCodes);

currencyCodes.forEach((cc) => {
// console.log(res.data.results[cc].currencyName);
countryCodes.push({
currencyName: res.data.results[cc].currencyName,
currencyCode: cc
})
})

this.setState({
currencyCodes: countryCodes
})


}).catch((err) => {
console.log(err)
})
}

convertCurrency = () => {
if(this.state.tocountry === '' && this.state.frcountry === '') {
alert("pls select a county")
}
if(this.state.amount === '') {
alert("pls Enter Amount")
}

// validation for amount
let q = `${this.state.tocountry}_${this.state.frcountry}`
axios({
method:'GET',
url:`https://free.currconv.com/api/v7/convert?q=${q}&apiKey=b2926387e0a03a02fa74&compact=ultra`
}).then((res)=>{
console.log(res.data[q]);

let convertedValue = 0;
convertedValue=this.state.amount*res.data[q];

this.setState({
convertedValue:convertedValue,
amount:""
})

}).catch((err)=>{
console.log(err)
})
}

render() {
return (
<div className="App">
  <section class="hero is-medium is-primary is-bold">
    <div class="hero-body">

    <div class="container">
<br/>
    Cureency Converter React js 
    <hr/>

              </div>
      <div class="container">
        <div class="columns is-desktop">
          <div class="column">
          <div class="control has-icons-left has-icons-right">
  <input class="input" class="input is-rounded" type="" placeholder="Email"
  value={this.state.amount} onChange={(e) => {
    this.setState({
      amount: e.target.value
    })
  }} 
  />
  <span class="icon is-small is-left">
    <i class="fas fa-envelope"></i>
  </span>
  <span class="icon is-small is-right">
    <i class="fas fa-check"></i>
  </span>
</div>
          </div>
          <div class="column">
            <div class="field">
              <div class="control has-icons-left">
                <div class="select is-rounded is-fullwidth">
                  <select>
                    <option selected>Country</option>
                    <option>Select dropdown</option>
                    <option>With options</option>
                  </select>
                </div>
                <div class="icon is-small is-left">
                  <i class="fas fa-globe"></i>
                </div>
              </div>
            </div>
          </div>
          <div class="column">
          <div class="field">
              <div class="control has-icons-left">
                <div class="select is-rounded is-fullwidth">
                  <select>
                    <option selected>Country 2</option>
                    <option>Select dropdown</option>
                    <option>With options</option>
                  </select>
                </div>
                <div class="icon is-small is-left">
                  <i class="fas fa-globe"></i>
                </div>
              </div>
            </div>
          </div>
          <div class="column">
          <a class="button is-primary is-inverted is-rounded is-fullwidth">Convert</a>
          </div>
        </div>
      </div>
      <div class="container">
<br/>
    This container is <strong>centered</strong> on desktop.

              </div>
    </div>
  </section>
</div>
);
}
}

export default App;
