import React ,{Component} from 'react';
import logo from './logo.svg';
import './App.css';
import axios from 'axios';

class App extends Component {

  constructor(props) {
    super(props);

    this.state = {
      amount: 0,
      country: '',
      convertedValue: 0,
      currencyCodes: []
    }
  }

  componentWillMount = () => {
    console.log("will mount")
  }

  componentDidMount = () => {
    console.log("did mount");
    axios({
      method: "GET",
      url: "https://free.currconv.com/api/v7/currencies?apiKey=b2926387e0a03a02fa74"
    }).then((res) => {
      let countryCodes = [];
      console.log(res.data.results);
      let currencyCodes = Object.keys(res.data.results);
      console.log(currencyCodes);

      currencyCodes.forEach((cc) => {
        console.log(cc)
        countryCodes.push({
          currencyName: res.data.results[cc].currencyName,
          currencyCode: cc
        })
      })

      this.setState({
        currencyCodes
      })


    }).catch((err) => {
      console.log(err)
    })
  }

  convertCurrency = () => {
    if(this.state.country === '') {
      alert("pls select a county")
    }
    // validation for amount


    let convertedValue = 0;

    switch(this.state.country){
      case 'USD':
        convertedValue=this.state.amount*180;
        break;
      case 'INR' ://alert("INR");
        convertedValue=this.state.amount*2.50;
        break;
      case 'EUR'://alert("EUR");
        convertedValue=this.state.amount*320;
        break;
    }
    this.setState({
      convertedValue:convertedValue,
      amount:""
    })

  }

  render() {
    return (
      <div className="App">

        <input type="number" value={this.state.amount} onChange={(e) => {
          this.setState({
            amount: e.target.value
          })
        }} ></input>

        <select onChange={(e) => {
          this.setState({
            country: e.target.value
          })
        }}>
          <option value="">--- select ---</option>
          <option value="USD">USD</option>
          <option value="EUR">EUR</option>
          <option value="INR">INR</option>
        </select>

        <br />

        <button onClick={this.convertCurrency}>Convert</button>

        <div>{this.state.convertedValue}</div>

      </div>
    );    
  }
}

export default App;
